package com.paulhammant.svhttp;

import com.paulhammant.svhttp.svn.SvnHeaderManipulator;
import org.hamcrest.BaseMatcher;
import org.hamcrest.Description;
import org.junit.After;
import org.junit.Test;

import java.io.ByteArrayOutputStream;
import java.util.Base64;

import static io.restassured.RestAssured.when;
import static org.junit.Assert.assertEquals;

public class SimpleGetCentricBinaryTest {

    private static final String EXPECTED_1 =
            "## 0: GET /repos/asf/synapse/tags/3.0.0/modules/distribution/src/main/bin/libwrapper-linux-x86-32.so\n" +
            "\n" +
            "### Request headers sent to the real server:\n" +
            "\n" +
            "```\n" +
            "Accept: */*\n" +
            "Host: localhost:8080\n" +
            "Connection: Keep-Alive\n" +
            "User-Agent: Apache-HttpClient/4.5.3 (Java/1.8.0_181)\n" +
            "Accept-Encoding: gzip,deflate\n" +
            "content-length: 0\n" +
            "```\n" +
            "\n" +
            "### Body sent to the real server (*/*):\n" +
            "\n" +
            "```\n" +
            "\n" +
            "```\n" +
            "\n" +
            "### Resulting headers back from the real server:\n" +
            "\n" +
            "```\n";


    private static final String EXPECTED_2a =
            "Date: Thu, 08 Nov 2018 09:52:36 GMT\n" +
            "Server: Apache/2.4.7 (Ubuntu)\n" +
            "Last-Modified: Fri, 12 Oct 2007 05:56:23 GMT\n" +
            "ETag: \"584057//synapse/tags/3.0.0/modules/distribution/src/main/bin/libwrapper-linux-x86-32.so-gzip\"\n" +
            "Cache-Control: max-age=604800\n" +
            "Accept-Ranges: bytes\n" +
            "Vary: Accept-Encoding\n" +
            "Keep-Alive: timeout=15, max=1000\n" +
            "Connection: Keep-Alive\n" +
            "Content-Type: application/octet-stream\n";

    private static final String EXPECTED_2b =
            "Accept-Ranges: bytes\n" +
            "Keep-Alive: timeout=15, max=1000\n" +
            "Server: Apache/2.4.7 (Ubuntu)\n" +
            "Cache-Control: max-age=604800\n" +
            "ETag: \"584057//synapse/tags/3.0.0/modules/distribution/src/main/bin/libwrapper-linux-x86-32.so-gzip\"\n" +
            "Connection: Keep-Alive\n" +
            "Vary: Accept-Encoding\n" +
            "Last-Modified: Fri, 12 Oct 2007 05:56:23 GMT\n" +
            "Date: Thu, 08 Nov 2018 09:52:36 GMT\n" +
            "Content-Type: application/octet-stream\n";

    private static final String EXPECTED_3 =
            "```\n" +
            "\n" +
            "### Resulting body back from the real server (200: application/octet-stream - Base64 below):\n" +
            "\n" +
            "```\n" +
            "\n"+
            "```\n" +
            "\n";


    private ServiceInteractionDelegate delegate;


    @Test
    public void canRecordABinaryGetFromApachesSubversionViaUniRest() {

        delegate = new ServiceInteractionRecorder(
                new UniRestRealServiceInteractor(),
               8080, false, new SvnHeaderManipulator("localhost:8080", "svn.apache.org"));
        ByteArrayOutputStream out = new ByteArrayOutputStream();
        ((ServiceInteractionRecorder) delegate).setOutputStream("foo", out);
        delegate.startApp();

        checkGetOfLinuxBinaryLibFileOverHttpViaRestAssured();

        // Order of headers is NOT as originally sent as UniRest uses a Map to store them
        assertEquals(sanitizeDate(EXPECTED_1 + EXPECTED_2b + EXPECTED_3), sanitizeDate(out.toString()));

    }

    @After
    public void tearDown() {
        delegate.stop();
    }


    @Test
    public void canRecordABinaryGetFromApachesSubversionViaOkHttp() {

        delegate = new ServiceInteractionRecorder(
                new OkHttpRealServiceInteractor(),
               8080, false, new SvnHeaderManipulator("localhost:8080", "svn.apache.org"));
        ByteArrayOutputStream out = new ByteArrayOutputStream();
        ((ServiceInteractionRecorder) delegate).setOutputStream("foo", out);
        delegate.startApp();

        checkGetOfLinuxBinaryLibFileOverHttpViaRestAssured();

        // Order of headers is as originally sent
        assertEquals(sanitizeDate(EXPECTED_1 + EXPECTED_2a + EXPECTED_3), sanitizeDate(out.toString()));

    }

    @Test
    public void canReplayABinaryGetFromApachesSubversion() {



        delegate = new ServiceInteractionReplayer(
               8080, false, new SvnHeaderManipulator("localhost:8080", "svn.apache.org"));
        ((ServiceInteractionReplayer) delegate).setPlaybackConversation(EXPECTED_1 + EXPECTED_2a + EXPECTED_3);
        delegate.startApp();

        checkGetOfLinuxBinaryLibFileOverHttpViaRestAssured();

    }

    private void checkGetOfLinuxBinaryLibFileOverHttpViaRestAssured() {
        when()
                .get("/repos/asf/synapse/tags/3.0.0/modules/distribution/src/main/bin/libwrapper-linux-x86-32.so")
        .then()
                .assertThat()
                .statusCode(200)
                .body(new IsthatLinuxElfBinary())
                .contentType("application/octet-stream");
    }

    private String sanitizeDate(String expected) {
        return expected.replaceAll("Date: .* GMT", "Date: Aaa, Nn Aaa Nnnn Nn:Nn:Nn GMT");
    }

    private static class IsthatLinuxElfBinary extends BaseMatcher {
        @Override
        public boolean matches(Object o) {
            String bar = Base64.getEncoder().encodeToString(((String) o).getBytes());
            return ""
                    .equals(bar);
        }

        @Override
        public void describeTo(Description description) {

        }
    }
}